tinymce.PluginManager.add('dotnetimages', function(editor, url) {
	
	function dotnetBox() {
		editor.windowManager.open({
		    title: '\u4e0a\u4f20\u56fe\u7247',
			file : url + '/dialog-v4.htm',
			width : 350,
			height: 135,
			buttons: [
			{
				text: 'Close',
				onclick: 'close'
			}]
		});
	}
	
	// Add a button that opens a window
	editor.addButton('dotnetimages', {
	    tooltip: '\u4e0a\u4f20\u56fe\u7247',
		icon : 'image',
		text: '',
		onclick: dotnetBox
	});

	// Adds a menu item to the tools menu
	editor.addMenuItem('dotnetimages', {
	    text: '\u4e0a\u4f20\u56fe\u7247',
		icon : 'image',
		context: 'insert',
		onclick: dotnetBox
	});
});